/*---------------------------------------------------------------*/
/* Global Style
/*---------------------------------------------------------------*/

html,body {width:100%;}

body {
	margin: 0px;
	font-family: HelvNeuRoman, Arial, Gotham, "Helvetica Neue", Helvetica, sans-serif;
	font-size: 13px;
	color: #555;
}

h1 {
	font-family: HelvNeuMed, Arial, Gotham, "Helvetica Neue", Helvetica, sans-serif;
	font-weight: normal;
	font-size: 34px;
	letter-spacing: -0.01em;
	color: #000;
}

h2 {
	font-size: 20px;
	font-family: HelvNeuBold, Arial, Gotham, "Helvetica Neue", Helvetica, sans-serif;
	font-weight: normal;
	background-color: #eee;
	color: #222;
	padding: 15px;
}

h3 {
	font-size: 16px;
	font-family: HelvNeuRoman, Arial, Gotham, "Helvetica Neue", Helvetica, sans-serif;
	font-weight: normal;
	color: #222;
}

p {
	font-size: 13px;
	line-height: 18px;
}

a {
	color: #cd239d;
	text-decoration: none;
}
a:hover {
	color: #555;
}

.content {
    max-width: 960px;
    position: relative;
    margin: 0 auto;
}

